import { motion } from 'framer-motion';

const Portfolio = () => {
  const poetryProjects = [
    {
      title: "Soupy Writes",
      description: "A collection of original poetry exploring themes of love, life, and human experience.",
      platform: "Instagram",
      link: "https://www.instagram.com/soupy_writes/",
      featured: true
    }
  ];

  const creativeWorks = [
    {
      title: "Poetry Series",
      description: "Regular poetry updates sharing personal stories and observations through verse.",
      platform: "Instagram"
    },
    {
      title: "Audio Poetry",
      description: "Spoken word recordings bringing poetry to life through voice and emotion.",
      platform: "Instagram"
    }
  ];

  return (
    <div className="max-w-4xl mx-auto pt-16">
      <motion.h1
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-4xl font-bold mb-8"
      >
        Creative Portfolio
      </motion.h1>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="mb-12"
      >
        <h2 className="text-2xl font-bold mb-4">Featured Work</h2>
        <div className="grid gap-8">
          {poetryProjects.map((project, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
              className="bg-gray-800 rounded-lg p-6 hover:shadow-lg transition-shadow"
            >
              <h3 className="text-xl font-semibold mb-2">{project.title}</h3>
              <p className="mb-4 text-gray-300">{project.description}</p>
              <div className="flex items-center space-x-4">
                <span className="px-3 py-1 bg-blue-500 rounded-full text-sm">
                  {project.platform}
                </span>
                <a
                  href={project.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-blue-400 hover:text-blue-300 transition-colors"
                >
                  View on Instagram
                </a>
              </div>
            </motion.div>
          ))}
        </div>
      </motion.div>

      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <h2 className="text-2xl font-bold mb-4">Creative Works</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {creativeWorks.map((work, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.2 }}
              className="bg-gray-800 rounded-lg p-4 hover:bg-gray-700 transition-colors"
            >
              <h3 className="font-semibold mb-2">{work.title}</h3>
              <p className="text-gray-300">{work.description}</p>
              <span className="mt-2 px-3 py-1 bg-blue-500 rounded-full text-sm">
                {work.platform}
              </span>
            </motion.div>
          ))}
        </div>
      </motion.div>
    </div>
  );
};

export default Portfolio;
